contracts/ERC6551/ERC6551Manager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import "../Interfaces/IERC6551Registry.sol";
import "../Interfaces/IERC6551Proxy.sol";
import "../Helpers/Validator.sol";

/// @title ERC6551Manager contract
/// @author Venkatesh
/// @notice This contract is used to manage the ERC6551 funtionalities
/// @custom:security-contact rvenki666@gmail.com
contract ERC6551Manager is AccessControlEnumerable {
    // ERC721 interface id to check the ERC721 compatibility
    bytes4 private constant INTERFACE_ID_ERC721 = 0x80ac58cd;

    // Use 0 bytes32 value as salt - It is officially used by tokenbound.orgs
    bytes32 public erc6551Salt;

    // check the proxy contracts from here => https://docs.tokenbound.org/contracts/deployments
    address public erc6551ProxyAddress;

    // check the implementation contracts from here => https://docs.tokenbound.org/contracts/deployments
    address public erc6551ImplementationAddress;

    // check the registry contracts from here => https://docs.tokenbound.org/contracts/deployments
    address public erc6551RegistryAddress;

    /// @notice Event emitted when the ERC6551 registry is updated
    event ERC6551RegistryUpdated(
        address oldRegistryAddress,
        address newRegistryAddress
    );

    /// @notice Event emitted when the ERC6551 proxy is updated
    event ERC6551ProxyUpdated(
        address oldProxyAddress,
        address newProxyAddress
    );

    /// @notice Event emitted when the ERC6551 implementation is updated
    event ERC6551ImplementationUpdated(
        address oldImplementationAddress,
        address newImplementationAddress
    );

    /// @notice Event emitted when the ERC6551 salt is updated
    event ERC6551SaltUpdated(bytes32 oldSalt, bytes32 newSalt);


    /// @notice Constructor for the ERC6551Manager contract
    /// @param _registryAddress The address of the ERC6551 registry
    /// @param _proxyAddress The address of the ERC6551 proxy
    /// @param _implementationAddress The address of the ERC6551 implementation
    /// @param _salt The salt value for the ERC6551
    /// @param _owner The owner of the contract
    constructor(
        address _registryAddress,
        address _proxyAddress,
        address _implementationAddress,
        bytes32 _salt,
        address _owner
    ) {
        Validator.checkForZeroAddress(_registryAddress);
        Validator.checkForZeroAddress(_proxyAddress);
        Validator.checkForZeroAddress(_implementationAddress);
        Validator.checkForZeroAddress(_owner);
        Validator.checkForZeroBytes32(_salt);

        _grantRole(DEFAULT_ADMIN_ROLE, _owner);

        erc6551RegistryAddress = _registryAddress;
        erc6551ProxyAddress = _proxyAddress;
        erc6551ImplementationAddress = _implementationAddress;
        erc6551Salt = _salt;
    }

    /// @notice Configure the ERC 6551 registry contract address to lookup/create TBA
    /// @param _registryAddress ERC 6551 registry contract address
    function setupERC6551Registry(
        address _registryAddress
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Validator.checkForZeroAddress(_registryAddress);

        address oldRegistryAddress = erc6551RegistryAddress;
        erc6551RegistryAddress = _registryAddress;

        emit ERC6551RegistryUpdated(oldRegistryAddress, _registryAddress);
    }

    /// @notice Configure the ERC 6551 proxy contract address for TBA
    /// @param _proxyAddress ERC 6551 proxy contract address
    function setupERC6551Proxy(
        address _proxyAddress
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Validator.checkForZeroAddress(_proxyAddress);

        address oldProxyAddress = erc6551ProxyAddress;
        erc6551ProxyAddress = _proxyAddress;

        emit ERC6551ProxyUpdated(
            oldProxyAddress,
            _proxyAddress
        );
    }

    /// @notice Configure the ERC 6551 implementation contract address for TBA
    /// @param _implementationAddress ERC 6551 implementation contract address
    function setupERC6551Implementation(
        address _implementationAddress
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Validator.checkForZeroAddress(_implementationAddress);

        address oldImplementationAddress = erc6551ImplementationAddress;
        erc6551ImplementationAddress = _implementationAddress;

        emit ERC6551ImplementationUpdated(
            oldImplementationAddress,
            _implementationAddress
        );
    }

    /// @notice Configure the salt for the creation and lookup of token bound account
    /// @param _salt ERC 6551 salt value (zero is officially used)
    function setupERC6551Salt(bytes32 _salt) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Validator.checkForZeroBytes32(_salt);

        bytes32 oldSalt = erc6551Salt;
        erc6551Salt = _salt;

        emit ERC6551SaltUpdated(oldSalt, _salt);
    }

    /// @notice Determine or retrieve the NFT's token bound account address
    /// @param _nftContractAddress NFT contract address
    /// @param _tokenId NFT token ID
    /// @return tokenBoundAccountAddress NFT's token bound address
    function getTokenBoundAccount(
        address _nftContractAddress,
        uint256 _tokenId
    ) external view returns (address tokenBoundAccountAddress) {
        Validator.checkForZeroAddress(_nftContractAddress);
        Validator.checkSupportsInterface(
            _nftContractAddress,
            INTERFACE_ID_ERC721
        );

        tokenBoundAccountAddress = IERC6551Registry(erc6551RegistryAddress)
            .account(
                erc6551ProxyAddress,
                erc6551Salt,
                _getChainId(),
                _nftContractAddress,
                _tokenId
            );
    }

    /// @notice Deploy the NFT's token bound account address if it is not already deployed
    /// @param _nftContractAddress NFT contract address
    /// @param _tokenId NFT token ID
    /// @return tokenBoundAccountAddress NFT's token bound address
    function createTokenBoundAccount(
        address _nftContractAddress,
        uint256 _tokenId
    ) external returns (address tokenBoundAccountAddress) {
        Validator.checkForZeroAddress(_nftContractAddress);
        Validator.checkSupportsInterface(
            _nftContractAddress,
            INTERFACE_ID_ERC721
        );

        tokenBoundAccountAddress = IERC6551Registry(erc6551RegistryAddress)
            .createAccount(
                erc6551ProxyAddress,
                erc6551Salt,
                _getChainId(),
                _nftContractAddress,
                _tokenId
            );

        IERC6551Proxy(payable(tokenBoundAccountAddress)).initialize(erc6551ImplementationAddress);
    }

    /// @notice Retrieve the chain id of the network where the contract is deployed
    /// @return id chain id of the network
    function _getChainId() internal view virtual returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }
}

contracts/Factory/SparkIdentityTokenFact.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "solmate/src/utils/CREATE3.sol";
import "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import "../Helpers/Validator.sol";

/// @title SparkIdentityTokenFactory contract
/// @author Venkatesh
/// @notice This contract is used to manage the deployment of SparkIdentityToken contracts
/// @custom:security-contact rvenki666@gmail.com
contract SparkIdentityTokenFactory is AccessControlEnumerable {
    bytes32 public constant DEPLOYER_ROLE = keccak256("DEPLOYER_ROLE");

    error DeployerRoleMissing();
    error InvalidSalt();
    error InvalidBytecode();

    /// @notice Event emitted when a new SparkIdentityToken contract is deployed
    event SparkIdentityTokenContractDeployed(
        address indexed contractAddress,
        address indexed deployedBy
    );

    /// @notice Modifier to allow only deployer and admin roles to call a function
    modifier onlyDeployer() {
        if (
            !hasRole(DEPLOYER_ROLE, _msgSender()) &&
            !hasRole(DEFAULT_ADMIN_ROLE, _msgSender())
        ) {
            revert DeployerRoleMissing();
        }
        _;
    }

    /// @notice Constructor for the SparkIdentityTokenFactory contract
    /// @param _owner The owner of the contract
    constructor(address _owner) {
        Validator.checkForZeroAddress(_owner);

        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _grantRole(DEPLOYER_ROLE, _owner);
    }

    /// @notice Deploys a new contract using CREATE3
    /// @param _amount The amount of ether to send with the contract
    /// @param _salt The salt value for the contract
    /// @param _bytecode The bytecode of the contract
    /// @return deployedContractAddress The address of the deployed contract
    function determinsiticDeploy(
        uint256 _amount,
        bytes32 _salt,
        bytes calldata _bytecode
    ) external onlyDeployer returns (address deployedContractAddress) {
        if (_salt.length == 0) {
            revert InvalidSalt();
        }

        if (_bytecode.length == 0) {
            revert InvalidBytecode();
        }

        deployedContractAddress = CREATE3.deploy(_salt, _bytecode, _amount);

        Validator.checkForZeroAddress(deployedContractAddress);

        emit SparkIdentityTokenContractDeployed(deployedContractAddress, msg.sender);
    }

    /// @notice Computes the address of a contract deployed with CREATE3
    /// @param _salt The salt value used in the deployment
    /// @return contractAddress The address of the contract
    function computeAddress(
        bytes32 _salt
    ) external view returns (address contractAddress) {
        contractAddress = CREATE3.getDeployed(_salt);
    }
}

contracts/Factory/SparkRegistryFactory.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "solmate/src/utils/CREATE3.sol";
import "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import "../Helpers/Validator.sol";

/// @title SparkRegistryFactory Contract
/// @notice This contract is used for deploying new SparkRegistry contracts deterministically using CREATE3
/// @dev Inherits AccessControlEnumerable for role-based permission management
/// @author Venkatesh
/// @custom:security-contact rvenki666@gmail.com
contract SparkRegistryFactory is AccessControlEnumerable {
    /// @dev Role identifier for deployers
    bytes32 public constant DEPLOYER_ROLE = keccak256("DEPLOYER_ROLE");

    /// @dev Custom error for missing deployer role
    error DeployerRoleMissing();
    /// @dev Custom error for invalid salt input
    error InvalidSalt();
    /// @dev Custom error for invalid bytecode input
    error InvalidBytecode();

    /// @notice Emitted when a new SparkRegistry contract is deployed
    /// @param contractAddress The address of the deployed contract
    /// @param deployedBy The address of the deployer
    event SparkRegistryContractDeployed(
        address indexed contractAddress,
        address indexed deployedBy
    );

    /// @notice Modifier to restrict function access to deployers and admins
    modifier onlyDeployer() {
        if (
            !hasRole(DEPLOYER_ROLE, _msgSender()) &&
            !hasRole(DEFAULT_ADMIN_ROLE, _msgSender())
        ) {
            revert DeployerRoleMissing();
        }
        _;
    }

    /// @notice Constructor to set up the contract with initial roles
    /// @param _owner The address to be granted admin and deployer roles
    constructor(address _owner) {
        Validator.checkForZeroAddress(_owner);

        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _grantRole(DEPLOYER_ROLE, _owner);
    }

    /// @notice Deploys a new SparkRegistry contract using CREATE3
    /// @dev Emits a SparkRegistryContractDeployed event upon successful deployment
    /// @param _amount The amount of ether to send with the contract creation
    /// @param _salt The salt to use for deterministic deployment
    /// @param _bytecode The bytecode of the contract to deploy
    /// @return deployedContractAddress The address of the deployed contract
    function determinsiticDeploy(
        uint256 _amount,
        bytes32 _salt,
        bytes calldata _bytecode
    ) external onlyDeployer returns (address deployedContractAddress) {
        if (_salt.length == 0) {
            revert InvalidSalt();
        }

        if (_bytecode.length == 0) {
            revert InvalidBytecode();
        }

        deployedContractAddress = CREATE3.deploy(_salt, _bytecode, _amount);

        Validator.checkForZeroAddress(deployedContractAddress);

        emit SparkRegistryContractDeployed(deployedContractAddress, msg.sender);
    }

    /// @notice Computes the address of a contract deployed with a specific salt via CREATE3
    /// @param _salt The salt used during deployment
    /// @return contractAddress The address of the contract deployed with the given salt
    function computeAddress(
        bytes32 _salt
    ) external view returns (address contractAddress) {
        contractAddress = CREATE3.getDeployed(_salt);
    }
}

contracts/Helpers/Validator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";

/// @title A library for validating various conditions in smart contracts
library Validator {    
    /// @dev Error to be thrown when a zero address is provided
    error ZeroAddressNotAllowed();
    /// @dev Error to be thrown when an empty bytes32 value is provided
    error InvalidBytes32();
    /// @dev Error to be thrown when an empty bytes value is provided
    error InvalidBytes();
    /// @dev Error to be thrown when a contract does not support a required interface
    error IncompatibleNFTContract();

    /// @notice Validates that an address is not the zero address
    /// @param _address The address to validate
    function checkForZeroAddress(address _address) internal pure {
        if (_address == address(0)) {
            revert ZeroAddressNotAllowed();
        }
    }

    /// @notice Validates that a bytes32 value is not empty
    /// @param value The bytes32 value to validate
    function checkForZeroBytes32(bytes32 value) internal pure {
        if (value.length == 0) {
            revert InvalidBytes32();
        }
    }

    /// @notice Validates that a bytes value is not empty
    /// @param value The bytes value to validate
    function checkForZeroBytes(bytes memory value) internal pure {
        if (value.length == 0) {
            revert InvalidBytes();
        }
    }

    /// @notice Checks if a contract supports a specific interface according to ERC165
    /// @param _contractAddress The address of the contract to check
    /// @param _interfaceId The interface identifier to check for
    function checkSupportsInterface(
        address _contractAddress,
        bytes4 _interfaceId
    ) internal view {
        bool isSupported = ERC165Checker.supportsInterface(
            _contractAddress,
            _interfaceId
        );

        if (!isSupported) {
            revert IncompatibleNFTContract();
        }
    }
}

